import React, { useState } from "react";
import "./Faqs.css";

const Faqs = () => {
  const [accordian, setAccordian] = useState(-1);

  const toggle = (index) => {
    setAccordian(index);
  };

  return (
    <>
      <div className="faqs">
        <div className="container">
          <div className="faqs-title">
            <p>Faqs</p>
          </div>

          <div className="accordian">
            {accordianData &&
              accordianData.map((data, index) => {
                return (
                  <div key={index} onClick={() => toggle(index)}>
                    <div className="faqs-heading">
                      <h3 className={accordian === index ? "active" : ""}>
                        {data.question}
                      </h3>
                    </div>
                    <div>
                      {accordian === index ? (
                        <>
                          <div className="vertical"> - </div>
                        </>
                      ) : (
                        <>
                          <div className="vertical"> + </div>
                        </>
                      )}
                    </div>

                    <div className="faqs-content">
                      <p>{data.answer}</p>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    </>
  );
};

