import React, { useState } from "react";
import "./Faqs.css";

const Faqs = () => {
  const [accordian, setAccordian] = useState(-1);

  const toggle = (index) => {
    if (index === accordian) {
      setAccordian(-1);
      return;
    }
    setAccordian(index);
  };

  return (
    <>
      <div className="faqs">
        <div className="container">
          <div className="faqs-title">
            <p>Faqs</p>
          </div>

          <div className="accordian">
            {accordianData &&
              accordianData.map((data, index) => {
                return (
                  <div key={index} onClick={() => toggle(index)}>
                    <div className="wrapperFlex">
                      <div className="faqs-heading">
                        <h2 className={accordian === index ? "active" : ""}>
                          {data.question}
                        </h2>
                      </div>
                      <div className="vertical">
                        {accordian === index ? (
                          <>
                            <p> - </p>
                          </>
                        ) : (
                          <>
                            <p> + </p>
                          </>
                        )}
                      </div>
                    </div>

                    <div className="faqs-content">
                      <p
                        className={accordian === index ? "active" : "inactive"}
                      >
                        {data.answer}
                      </p>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    </>
  );
};

export default Faqs;

